<?phpclass view {    protected $data = [];    protected $view;    protected $template;    public function setView($view, $layout = "template") {        // $view = indexIndex        $path_view = "views/" . $view . ".php";        $path_template = "views/" . $layout . ".php";        if (file_exists($path_view)) {            $this->view = $path_view;error_reporting(E_ALL);ini_set('display_errors', 1);            if (file_exists($path_template)) {                $this->template = $path_template;            } else {                die("Le template n'existe pas");            }        } else {            die("La vue n'existe pas");        }    }    public function assign($key, $value) {        $this->data[$key] = $value;    }    public function __destruct() {        extract($this->data);        include $this->template;    }}